	static public function installDataByUid($tbl, $uid, $data) {
		global $wpdb;
		$id = (int) $wpdb->get_var(
			$wpdb->prepare("SELECT id FROM {$wpdb->prefix}pts_tables WHERE unique_id = %s AND original_id = 0 AND is_base = 1", $uid)
		);
		$data['html'] = str_replace(array("\\n", "\\r", "\\r\\n"), '', $data['html']);
		$data['html'] = str_replace("\\", '', $data['html']);
		$action = $id ? 'UPDATE' : 'INSERT INTO';
		if($action == 'UPDATE') {
			$tableName = $wpdb->prefix . "pts_tables";
			$data_update = array(
				'unique_id' => $data['unique_id'],
				'label' => $data['label'],
				'original_id' => $data['original_id'],
				'params' => $data['params'],
				'html' => $data['html'],
				'css' => $data['css'],
				'img' => $data['img'],
				'sort_order' => $data['sort_order'],
				'is_base' => $data['is_base'],
				'is_pro' => $data['is_pro'],
				'date_created' => $data['date_created'],
			);
			$data_where = array('unique_id' => $uid, 'original_id' => 0, 'is_base' => 1);
			$res = $wpdb->update($tableName , $data_update, $data_where);
		} else {
			$tableName = $wpdb->prefix . "pts_tables";
			$res = $wpdb->insert($tableName, array(
					'unique_id' => $data['unique_id'],
					'label' => $data['label'],
					'original_id' => $data['original_id'],
					'params' => $data['params'],
					'html' => $data['html'],
					'css' => $data['css'],
					'img' => $data['img'],
					'sort_order' => $data['sort_order'],
					'is_base' => $data['is_base'],
					'is_pro' => $data['is_pro'],
					'date_created' => $data['date_created'],
			));
		}
		if($res) {
			$dataId = $action == 'UPDATE' ? $id : dbPts::insertID();
			$wrongId = (int) $wpdb->get_var(
				$wpdb->prepare("SELECT id FROM {$wpdb->prefix}pts_tables WHERE unique_id != %s AND original_id = 0 AND is_base = 1 AND label = %s", $uid, $data['label'])
			);
			if($wrongId) {
				$tableName = $wpdb->prefix . "pts_tables";
				$data_where = array('id' => $wrongId);
				$wpdb->delete( $tableName, $data_where);
				$data_update = array(
					'original_id' => $dataId,
				);
				$data_where = array('original_id' => $wrongId);
				$res = $wpdb->update($tableName , $data_update, $data_where);
			}
			return $dataId;
		}
		return false;
	}
